import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import "./Button.css";

const Button = ({ to, name }) => {
  let destination = "/" + to;

  useEffect(() => {
    initButton();

    function initButton() {
      const button = document.querySelector(".liquid-button");
      if (!button) return;

      const pointsA = [];
      const pointsB = [];
      const canvas = document.createElement("canvas");
      const context = canvas.getContext("2d");
      const points = 8;
      const viscosity = 20;
      const damping = 0.05;
      const mouseDist = 70;

      let mouseX = 0,
        mouseY = 0,
        relMouseX = 0,
        relMouseY = 0;

      const buttonWidth = button.offsetWidth;
      const buttonHeight = button.offsetHeight;
      canvas.width = buttonWidth + 100;
      canvas.height = buttonHeight + 100;
      button.appendChild(canvas);

      // Add points to the canvas for the liquid effect
      const x = buttonHeight / 2;
      for (let j = 1; j < points; j++) {
        addPoints(x + ((buttonWidth - buttonHeight) / points) * j, 0);
      }
      addPoints(buttonWidth - buttonHeight / 5, 0);
      addPoints(buttonWidth + buttonHeight / 10, buttonHeight / 2);
      addPoints(buttonWidth - buttonHeight / 5, buttonHeight);
      for (let j = points - 1; j > 0; j--) {
        addPoints(x + ((buttonWidth - buttonHeight) / points) * j, buttonHeight);
      }
      addPoints(buttonHeight / 5, buttonHeight);
      addPoints(-buttonHeight / 10, buttonHeight / 2);
      addPoints(buttonHeight / 5, 0);

      renderCanvas();

      function mouseDirection(e) {
        const rect = button.getBoundingClientRect();
        mouseX = e.clientX;
        mouseY = e.clientY;
        relMouseX = mouseX - rect.left;
        relMouseY = mouseY - rect.top;
      }

      document.addEventListener("mousemove", mouseDirection);

      // Ensure points are being added correctly
      function addPoints(x, y) {
        const pointA = new Point(x, y, 1);
        const pointB = new Point(x, y, 2);

        pointsA.push(pointA);
        pointsB.push(pointB);
      }

      function Point(x, y, level) {
        this.x = this.ix = 50 + x;
        this.y = this.iy = 50 + y;
        this.vx = 0;
        this.vy = 0;
        this.level = level;

        // Bind the move method to each instance
        this.move = function () {
          this.vx += (this.ix - this.x) / (viscosity * this.level);
          this.vy += (this.iy - this.y) / (viscosity * this.level);
          this.vx *= 1 - damping;
          this.vy *= 1 - damping;
          this.x += this.vx;
          this.y += this.vy;
        };
      }

      function renderCanvas() {
        try {
          requestAnimationFrame(renderCanvas);
          context.clearRect(0, 0, canvas.width, canvas.height);

          pointsA.forEach((point) => point.move());
          pointsB.forEach((point) => point.move());

          context.beginPath();
          context.moveTo(pointsA[0].x, pointsA[0].y);

          pointsA.forEach((point, i) => {
            const nextPoint = pointsA[i + 1] || pointsA[0];
            if (nextPoint && nextPoint.x !== undefined && nextPoint.y !== undefined) {
              context.quadraticCurveTo(point.x, point.y, nextPoint.x, nextPoint.y);
            }
          });

          context.closePath();
          context.fillStyle = "#1CE2D8";
          context.fill();
        } catch (error) {
          console.error("Error rendering canvas: ", error);
          cancelAnimationFrame(renderCanvas); // Cancel animation if there's an error
        }
      }
    }
  }, []);

  return (
    <Link to={destination} className="liquid-button">
      {name}
    </Link>
  );
};

export default Button;
