import React, { useEffect, useRef } from "react";
import Slider from "react-slick";
import "./Skills.css";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

// Import your images here
import html from "../../../images/homeimages/skillsimages/html.svg";
import css from "../../../images/homeimages/skillsimages/css.svg";
import javascript from "../../../images/homeimages/skillsimages/javascript.svg";
import reactjs from "../../../images/homeimages/skillsimages/react.svg";
import ruby from "../../../images/homeimages/skillsimages/ruby.svg";
import rubyonrails from "../../../images/homeimages/skillsimages/RoR.svg";
import postgresql from "../../../images/homeimages/skillsimages/postgresql.svg";

const Skills = () => {
  const skillRefs = useRef([]); // Create a ref array for skill items

  const settings = {
    dots: false,
    infinite: true,
    speed: 6000,
    slidesToShow: 6, // Default to show 6 slides
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 0,
    cssEase: "linear",
    arrows: false,
    draggable: true,
    swipe: true,
    swipeToSlide: true,
    responsive: [
      {
        breakpoint: 980,
        settings: {
          slidesToShow: 4,
        },
      },
    ],
  };

  // Animation handler using Intersection Observer
  useEffect(() => {
    const observer = new IntersectionObserver((entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          entry.target.classList.add("fade-in");
        } else {
          entry.target.classList.remove("fade-in"); // Optional: remove class if out of view
        }
      });
    });

    // Attach observer to each skill item
    skillRefs.current.forEach((ref) => {
      if (ref) observer.observe(ref);
    });

    return () => {
      // Cleanup the observer on component unmount
      skillRefs.current.forEach((ref) => {
        if (ref) observer.unobserve(ref);
      });
    };
  }, []);

  return (
    <section className="skills">
      <h2 className="title fade-in">Skills</h2>
      <p className="skills-para fade-in">
        I have acquired a versatile set of skills through hands-on projects and continuous learning. Below, you'll find a showcase of the core technologies I specialize in, each one playing a crucial role in my journey as a full-stack developer.
      </p>
      <hr className="line fade-in" />
      <Slider {...settings}>
        {[
          { title: "HTML", img: html },
          { title: "CSS", img: css },
          { title: "JavaScript", img: javascript },
          { title: "React", img: reactjs },
          { title: "PostgreSQL", img: postgresql },
          { title: "Ruby", img: ruby },
          { title: "Ruby on Rails", img: rubyonrails },
        ].map((skill, index) => (
          <div className="inner-skill-pack" key={index} ref={(el) => (skillRefs.current[index] = el)}>
            <h3 className="skill-title">{skill.title}</h3>
            <img src={skill.img} alt={skill.title} />
          </div>
        ))}
      </Slider>
    </section>
  );
};

export default Skills;
