import React, { useEffect, useRef } from "react";
import "./Button.css";

const Button = ({ name }) => {
  const buttonRef = useRef(null); // Ref for the button element
  const canvasRef = useRef(null); // Ref for the canvas element

  useEffect(() => {
    const button = buttonRef.current;
    let canvas = canvasRef.current;
    let context = canvas.getContext("2d");
    const pointsA = [];
    const pointsB = [];
    const points = 8;
    const viscosity = 20;
    const damping = 0.05;
    const mouseDist = 70;
    let mouseX = 0, mouseY = 0;
    let relMouseX = 0, relMouseY = 0;
    let mouseSpeedX = 0, mouseSpeedY = 0;
    let mouseLastX = 0, mouseLastY = 0;
    let mouseDirectionX = 0, mouseDirectionY = 0;

    const buttonWidth = button.offsetWidth;
    const buttonHeight = button.offsetHeight;
    canvas.width = buttonWidth + 100;
    canvas.height = buttonHeight + 100;

    const x = buttonHeight / 2;
    for (let j = 1; j < points; j++) {
      addPoints(x + ((buttonWidth - buttonHeight) / points) * j, 0);
    }
    addPoints(buttonWidth - buttonHeight / 5, 0);
    addPoints(buttonWidth + buttonHeight / 10, buttonHeight / 2);
    addPoints(buttonWidth - buttonHeight / 5, buttonHeight);
    for (let j = points - 1; j > 0; j--) {
      addPoints(x + ((buttonWidth - buttonHeight) / points) * j, buttonHeight);
    }
    addPoints(buttonHeight / 5, buttonHeight);
    addPoints(-buttonHeight / 10, buttonHeight / 2);
    addPoints(buttonHeight / 5, 0);

    function addPoints(x, y) {
      pointsA.push(new Point(x, y, 1));
      pointsB.push(new Point(x, y, 2));
    }

    function Point(x, y, level) {
      this.x = this.ix = 50 + x;
      this.y = this.iy = 50 + y;
      this.vx = 0;
      this.vy = 0;
      this.level = level;
    }

    Point.prototype.move = function () {
      this.vx += (this.ix - this.x) / (viscosity * this.level);
      this.vy += (this.iy - this.y) / (viscosity * this.level);

      const dx = this.ix - relMouseX;
      const dy = this.iy - relMouseY;
      const relDist = (1 - Math.sqrt((dx * dx) + (dy * dy)) / mouseDist);

      if ((mouseDirectionX > 0 && relMouseX > this.x) || (mouseDirectionX < 0 && relMouseX < this.x)) {
        if (relDist > 0 && relDist < 1) {
          this.vx = (mouseSpeedX / 4) * relDist;
        }
      }
      this.vx *= (1 - damping);
      this.x += this.vx;

      if ((mouseDirectionY > 0 && relMouseY > this.y) || (mouseDirectionY < 0 && relMouseY < this.y)) {
        if (relDist > 0 && relDist < 1) {
          this.vy = (mouseSpeedY / 4) * relDist;
        }
      }
      this.vy *= (1 - damping);
      this.y += this.vy;
    };

    function renderCanvas() {
      requestAnimationFrame(renderCanvas);

      context.clearRect(0, 0, canvas.width, canvas.height);
      context.fillStyle = "#fff";
      context.fillRect(0, 0, canvas.width, canvas.height);

      pointsA.forEach(point => point.move());
      pointsB.forEach(point => point.move());

      context.beginPath();
      context.moveTo(pointsA[0].x, pointsA[0].y);

      pointsA.forEach((point, i) => {
        const nextPoint = pointsA[i + 1] || pointsA[0];
        const cx = (point.x + nextPoint.x) / 2;
        const cy = (point.y + nextPoint.y) / 2;
        context.quadraticCurveTo(point.x, point.y, cx, cy);
      });

      context.closePath();
      context.fillStyle = "#1CE2D8";
      context.fill();
    }

    renderCanvas();

    function mouseDirection(e) {
      const rect = button.getBoundingClientRect();
      mouseX = e.clientX;
      mouseY = e.clientY;
      relMouseX = mouseX - rect.left;
      relMouseY = mouseY - rect.top;
      if (mouseX > mouseLastX) mouseDirectionX = 1;
      else mouseDirectionX = -1;
      if (mouseY > mouseLastY) mouseDirectionY = 1;
      else mouseDirectionY = -1;
      mouseLastX = mouseX;
      mouseLastY = mouseY;
    }

    function mouseSpeed() {
      mouseSpeedX = mouseX - mouseLastX;
      mouseSpeedY = mouseY - mouseLastY;
      setTimeout(mouseSpeed, 50);
    }

    document.addEventListener("mousemove", mouseDirection);
    mouseSpeed();

    return () => {
      document.removeEventListener("mousemove", mouseDirection);
    };
  }, []);

  return (
    <div ref={buttonRef} className="btn-liquid">
      <canvas ref={canvasRef}></canvas>
      <span className="inner">{name}</span>
    </div>
  );
};

export default Button;
