import React, { useState, useEffect } from "react";
import "./Hero.css";
import Button from "../../../components/button/Button";

const Hero = () => {
  const [typedQuote, setTypedQuote] = useState("");
  const quote = "Knowing yourself is the beginning of all wisdom.";

  useEffect(() => {
    let index = -1;
    const typingInterval = setInterval(() => {
      if (index < quote.length) {
        setTypedQuote((prev) => prev + quote.charAt(index));
        index++;
      } else {
        clearInterval(typingInterval);
      }
    }, 100); // Adjust speed (in milliseconds) for the typing effect

    return () => clearInterval(typingInterval);
  }, [quote]);

  return (
    <section className="hero">
      <div className="hero-content">
        <h1>Hi, I'm Manuel Lipo</h1>
        <h2 className="hero-profession">Full-Stack Developer</h2>
        <blockquote className="quote">
          <span className="typed-text">{typedQuote}</span>
        </blockquote>
        <div className="hero-cta-buttons">
          <Button url={"/portfolio"} name={"My Work"} />
          <Button url={"/contact"} name={"Contact Me"} />
          <Button url={"/files/Manuel-Lipo-CV.pdf"} downloadLink={"Manuel-Lipo-CV.pdf"} name={"Download CV"} />
        </div>
      </div>
    </section>
  );
};

export default Hero;
