import React from 'react';

const Hero = () => {
    return (
        <section className="h-screen bg-cover bg-center flex items-center justify-center bg-[url('https://source.unsplash.com/random/1600x900/?web,development')]">
            <div className="text-center text-white">
                <h1 className="text-5xl md:text-6xl font-bold">Hi, I'm a Web Developer</h1>
                <p className="mt-4 text-lg md:text-2xl">Creating modern, responsive websites.</p>
                <a href="#projects" className="mt-8 inline-block bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-700 transition duration-300">View My Work</a>
            </div>
        </section>
    );
};

export default Hero;
