import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Header.css';
import Logo from './logo/Logo';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false); // State to toggle the menu

  // Toggle the menu open/close
  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <header>
      {/* Logo (centered on larger screens, left on smaller screens) */}
      <Link to="/" className="logo-link">
        <Logo />
      </Link>

      {/* Regular menu (shown only on large screens) */}
      <nav className="menu-left">
        <ul>
          <li><Link to="/">Home</Link></li>
          <li><Link to="/portfolio">Portfolio</Link></li>
        </ul>
      </nav>

      <nav className="menu-right">
        <ul>
          <li><Link to="/about">About</Link></li>
          <li><Link to="/contact">Contact</Link></li>
        </ul>
      </nav>

      {/* Burger icon for small screens */}
      <div className="burger-icon" onClick={toggleMenu}>
        <div className="line"></div>
        <div className="line"></div>
        <div className="line"></div>
      </div>

      {/* Full screen overlay menu (shown when burger is clicked) */}
      <div className={`overlay-menu ${isMenuOpen ? 'open' : ''}`}>
        <ul>
          <li><Link to="/" onClick={toggleMenu}>Home</Link></li>
          <li><Link to="/portfolio" onClick={toggleMenu}>Portfolio</Link></li>
          <li><Link to="/about" onClick={toggleMenu}>About</Link></li>
          <li><Link to="/contact" onClick={toggleMenu}>Contact</Link></li>
        </ul>
      </div>
    </header>
  );
};

export default Header;
